#!/bin/bash

mkdir azagent;cd azagent;curl -fkSL -o vstsagent.tar.gz https://vstsagentpackage.azureedge.net/agent/2.177.1/vsts-agent-linux-x64-2.177.1.tar.gz;tar -zxvf vstsagent.tar.gz; if [ -x "$(command -v systemctl)" ]; then ./config.sh --deploymentgroup --deploymentgroupname "mydevopstest" --acceptteeeula --agent $HOSTNAME --url https://dev.azure.com/manepallirajesh/ --work _work --projectname 'manepallirajesh' --runasservice; sudo ./svc.sh install; sudo ./svc.sh start; else ./config.sh --deploymentgroup --deploymentgroupname "mydevopstest" --acceptteeeula --agent $HOSTNAME --url https://dev.azure.com/manepallirajesh/ --work _work --projectname 'manepallirajesh'; ./run.sh; fi